            case .medium:
                inventory.shields.append(Shield(nineum: ninea))
            case .big:
                inventory.gloves.append(Gloves(nineum: ninea))
            case .large:
                inventory.necklaces.append(Necklace(nineum: ninea))
            case .huge:
                inventory.boots.append(Boots(nineum: ninea))
            }
        }*/
        
        while i < nineumArray.count {
            let ninea = nineumArray[i]
            i = i + 1
            switch ninea.size {
            case .small:
                inventory.weapons.append(Weapon(nineum: ninea))
            case .miniscule:
                inventory.helms.append(Helm(nineum: ninea))
            case .tiny:
                inventory.bodyArmor.append(BodyArmor(nineum: ninea))
            case .standard:
                inventory.bracers.append(Bracers(nineum: ninea))
            case .medium:
                inventory.shields.append(Shield(nineum: ninea))
            case .big:
                inventory.gloves.append(Gloves(nineum: ninea))
            case .large:
                inventory.necklaces.append(Necklace(nineum: ninea))
            case .huge:
                inventory.boots.append(Boots(nineum: ninea))
            }
        }
        
        inventory.weapons.sort(by: >)
        inventory.helms.sort(by: >)
        inventory.bodyArmor.sort(by: >)
        inventory.bracers.sort(by: >)
        inventory.shields.sort(by: >)
        inventory.gloves.sort(by: >)
        inventory.necklaces.sort(by: >)
        inventory.boots.sort(by: >)
        
        return inventory
    }
